// App.js
import React, { useEffect, useState } from "react";

function App() {
  const [backendStatus, setBackendStatus] = useState("Checking...");
  const [materials, setMaterials] = useState([]);

  useEffect(() => {
    // âœ… point directly to localhost
    fetch("http://localhost:5000/api/ping")
      .then((res) => res.json())
      .then((data) => setBackendStatus(data.message))
      .catch(() => setBackendStatus("Backend unreachable"));

    fetch("http://localhost:5000/api/raw-materials")
      .then((res) => res.json())
      .then((data) => setMaterials(data))
      .catch(() => setMaterials([]));
  }, []);

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h2>ðŸ”— Minimal Connectivity Test</h2>
      <p><strong>Backend status:</strong> {backendStatus}</p>
      <p><strong>API URL:</strong> http://localhost:5000</p>

      <h3>Raw Materials</h3>
      <ul>
        {materials.map((m) => (
          <li key={m.id}>{m.name} â€” {m.quantity}</li>
        ))}
      </ul>
    </div>
  );
}

export default App;
